(97, 1, 10, 'generate', 'y', 'webpage', '<?php\n$step = {embed:step};\n\n$sql = "SELECT * FROM `otca_quiz_questions` WHERE `step`=''$step'' ORDER BY RAND();";\n\n$query = $this->EE->db->query($sql);\n\nif ($query->num_rows() > 0)\n{\n\n	echo "<ol id=''quiz'' style=''min-height: 305px''>";\n	foreach($query->result_array() as $row)\n        {\n        $type = $row[''type''];\n        if($type == "MCQ") {\n		echo "<li style=''display:none; list-style: none''><p><pre style=''font-family: arial, helvetica, sans-serif; font-size: 12pt''>$row[question]</pre></p>";\n		$sql_answers = "SELECT * FROM `otca_quiz_answers` WHERE `question_id` = ''$row[id]'' AND `step` = ''$step'' ORDER BY RAND() LIMIT 0, 30 ";\n		$answer_query =  $this->EE->db->query($sql_answers);\n		echo "<ol style=''list-style-type: lower-alpha''>";\n		foreach($answer_query->result_array() as $answer_row)\n	        {\n                \n			if($answer_row[''correct''] == ''Y'') $correct = "value=''1''"; else 	$correct = "";\n			echo "<li><label><input name=''answer'' type=''radio'' data-feedback=''$answer_row[comment]'' $correct/> $answer_row[answer]</label></li>";\n                }\n		echo"</ol>";\n		echo "</li>";\n        }\n	if($type == "SAQ") {\n		$sql_answers = "SELECT `comment` FROM `otca_quiz_answers` WHERE `question_id` = ''$row[id]'' AND `step` = ''$step'' ORDER BY RAND() LIMIT 1";\n		$answer_query =  $this->EE->db->query($sql_answers);\n		foreach($answer_query->result_array() as $answer_row)\n	        {\n			echo "<li style=''display:none; list-style: none''><p><pre style=''font-family: arial, helvetica, sans-serif; font-size: 12pt''>$row[question]</pre></p><textarea id=''short_answer'' data-feedback=''$answer_row[comment]'' style=''min-width: 500px; min-height: 200px''></textarea></li>";\n		}\n	}\n	}\n	echo "</ol>";\n}\n?>', '', 1367994344, 1, 'n', 0, '', 'n', 'y', 'i', 9),
(98, 1, 1, 'generate', 'y', 'webpage', '<?php\n$member_id = $this->EE->session->userdata(''member_id'');\nif($member_id == 0) return;\n\n$element = $this->EE->input->get(''element'');\n\n$unit_code = substr($element, 0, 1);\n$unit_color = "unit$unit_code-color";\n$unit_heading = "unit$unit_code-heading";\n$unit_bgcolor = "unit$unit_code-bgcolor";\n$unit_sub_bgcolor = "unit$unit_code-sub-bgcolor";\n\nif(strlen($element) < 3) return;\n\n$sql = "SELECT `id`,`header` FROM `otca_pracsot_headers` WHERE id = ''$element''";\n\n$query = $this->EE->db->query($sql);\n\nif ($query->num_rows() > 0)\n{\n	foreach($query->result_array() as $row)\n    {\n		$header = $row[''header''];\n	}\n}\n\n$sql = "SELECT `id`, `question` FROM `otca_pracsot` WHERE `id` LIKE(''$element.%'')";\n\n$query = $this->EE->db->query($sql);\n\n$sql = "SELECT * FROM (select data.entry_id, data.field_id_6 as self_assessment, av.matrix_ids as supervisor_assessment, av.date_assessed FROM  exp_channel_data data LEFT JOIN (exp_channel_titles title , otca_evidence ev, otca_evidence_validated av) ON (data.entry_id = ev.entry_id AND title.entry_id = ev.entry_id AND ev.entry_id = av.evidence_id) WHERE title.author_id = ''2'' order by data.entry_id, av.date_assessed desc) ua GROUP BY ua.entry_id";\n\n$comparison_query = $this->EE->db->query($sql);\n\nif ($comparison_query->num_rows() > 0)\n{\n	foreach($comparison_query->result_array() as $row)\n    {	\n		// add option for self assessment later to compare the two...\n		$evidence_array[] = json_decode("[$row[supervisor_assessment]]");		\n		//$ev_validated[] = $row[''validated''];\n	}\n}\n?>	\n{embed=''pracsot/header''}\n<p>&nbsp;</p>\n{embed=''pracsot/<?php echo $unit_heading; ?> unit_color=''<?php echo $unit_color; ?>''}	\n		<table cellspacing="0" cellpadding="0" border="0" class="pracsot-table">\n			<colgroup>\n				<col width="513">\n				<col width="61">\n				<col width="61">\n				<col width="61">\n			</colgroup>\n			<tbody>\n				<tr class="tr-header">\n					<td class="td-header <?php echo $unit_bgcolor; ?>" style="text-align:left;width:11.746cm; font-size: 10pt;">\n						<p style="margin: 0.199cm;">\n							<span class="td-element-header">Element <?php echo $element; ?></span>&nbsp; &nbsp; &nbsp; &nbsp; <?php echo $header; ?>\n						</p>\n					</td>\n					<td class=''<?php echo $unit_bgcolor; ?>'' style="text-align:left; font-size: 10pt;" colspan="3" class="td-header">\n						<p class="p-demonstrated-competence">\n							Demonstrated Competence\n						</p>\n					</td>\n				</tr>\n				<tr>\n					<td class="perf-criteria <?php echo $unit_sub_bgcolor; ?>" style="text-align:left;width:11.746cm;">\n						<p class="perf-header">\n							Performance Criteria\n						</p>\n					</td>\n					<td class="perf-criteria <?php echo $unit_sub_bgcolor; ?>" style="text-align:left;width:2cm;">\n						<p class="perf-header">\n							Emerging\n						</p>\n					</td>\n					<td class="perf-criteria <?php echo $unit_sub_bgcolor; ?>" style="text-align:left;width:2cm;">\n						<p class="perf-header">\n							Consolidating\n						</p>\n					</td>\n					<td class="perf-criteria <?php echo $unit_sub_bgcolor; ?>" style="text-align: center;width: 3cm;">\n						<p class="perf-header">\n							Competent to Graduate\n						</p>\n					</td>\n				</tr>\n				\n<?php\n\nif ($query->num_rows() > 0)\n{\n	foreach($query->result_array() as $row)\n    {\n	\n?>\n				<tr>\n					<td style="text-align:left;width:11.746cm;">\n						<p>\n							<?php echo "<a name=''$row[id]''></a><strong>$row[id]</strong> - $row[question]"; ?>\n						</p>\n					</td>\n					\n<?php\n$emer_v = 0;\n$cons_v = 0;\n$comp_v = 0;\n\n$emer_u = 0;\n$cons_u = 0;\n$comp_u = 0;\n\nif(isset($evidence_array)) {\nforeach($evidence_array as $evidence)\n{	\n	$i = 0;\n	foreach($evidence as $ev) {\n		if(strpos($ev->pracsot, $row[''id'']) !== FALSE) {\n		\n			if($ev_validated[$i++] == ''TRUE'') {\n				switch($ev->level) {\n					case 1:\n						$emer_v = $emer_v + 1;\n					break;\n					case 2:\n						$cons_v = $cons_v + 1;\n					break;\n					case 3:\n						$comp_v = $comp_v + 1;\n				}\n			} else\n			{\n				switch($ev->level) {\n					case 1:\n						$emer_u = $emer_u + 1;\n					break;\n					case 2:\n						$cons_u = $cons_u + 1;\n					break;\n					case 3:\n						$comp_u = $comp_u + 1;\n				}\n			}\n		}   \n	}\n}\n}\n?>\n					<td tyle="text-align:left;width:1.386cm;">\n						<div title="Evidence - validated: <?php echo $emer_v; ?> unvalidated: <?php echo $emer_u; ?>" class="pracsot-tickbox">\n								<?php if($emer_v > 0) { ?>\n									<div style=''padding-top: 8px''>\n								<?php echo "&#10003;"; ?>\n									</div>\n							<?php	} ?>	\n						</div>\n					</td>\n					<td style="text-align:left;width:1.386cm;">\n						<div title="Evidence - validated: <?php echo $cons_v; ?> unvalidated: <?php echo $cons_u; ?>" class="pracsot-tickbox">\n								<?php if($cons_v > 0) { ?>\n									<div style=''padding-top: 8px''>\n								<?php echo "&#10003;"; ?>\n									</div>\n							<?php	} ?>\n						</div>\n					</td>\n					<td style="text-align:left;width:1.386cm;">\n						<div title="Evidence - validated: <?php echo $comp_v; ?> unvalidated: <?php echo $comp_u; ?>" class="pracsot-tickbox">\n						<?php if($comp_v > 0) { ?>\n							<div style=''padding-top: 8px''>\n						<?php echo "&#10003;"; ?>\n							</div>\n						<?php	} ?>\n						</div>\n					</td>\n				</tr>\n<?php\n	}\n}\n?>\n			</tbody>\n		</table>', NULL, 1362543057, 1, 'n', 0, '', 'n', 'n', 'o', 0);
